$(document).ready(function(){$('.black-overlay').fadeOut(1000);$('a').on('click',function(event){if(this.hash!==""){event.preventDefault();var hash=this.hash;$('html, body').animate({scrollTop:$(hash).offset().top},800,function(){})}})});var yourNavigation=$(".nav");var stickyDiv="nav-sticky";var yourHeader=$('#header-section').height();$(window).scroll(function(){if($(this).scrollTop()>yourHeader){yourNavigation.addClass(stickyDiv)}else{yourNavigation.removeClass(stickyDiv)}})